var modal=document.querySelector("#modal"),btn=document.querySelector("#modal-btn"),form=modal.querySelector("form"),startdate=modal.querySelector("[name=startdate]"),enddate=modal.querySelector("[name=enddate]"),adults=modal.querySelector("[name=adults]");btn.addEventListener("click",function(e){e.preventDefault(),modal.classList.contains("modal-hotels-hide")?(modal.classList.remove("modal-hotels-hide"),modal.classList.add("modal-hotels-show")):(modal.classList.add("modal-hotels-hide"),modal.classList.remove("modal-hotels-show"))}),form.addEventListener("submit",function(e){startdate.value&&enddate.value&&adults.value||(e.preventDefault(),alert("Проверьте правильность введеных параметров"))});